import React, { useState } from 'react'
import { Nav, Navbar, Form, FormControl, Button, Alert } from 'react-bootstrap'
import { useAuth } from "../../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"
import styled from 'styled-components'

export default function NavigationBar() {
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth()
    const history = useHistory()

    async function handleLogout() {
        setError('')

        try {
            await logout()
            history.pushState('/login')
        } catch {
            setError('Failed to log out')
        }
    }
    return (
        <Container>
            <Header>
            {error && <Alert variant="danger">{error}</Alert>}
            <Navbar expand="lg">
                <Navbar.Brand href="/">MOVIESRUS</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Form className="form-search">
                    <FormControl type="text" placeholder="Search" className="" />
                </Form>
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ml-auto">
                        <Nav.Item><Nav.Link href="/">Profile</Nav.Link></Nav.Item>
                        <Nav.Item>
                            <Button class="logoutBtn" variant="link" onClick={handleLogout}>Log Out</Button>
                        </Nav.Item>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
            </Header>
            <StyledSideNav></StyledSideNav>
        </Container>
    )
}


const Container = styled.div``;

const Header = styled.div`
    .navbar { background-color: #222; }
    a, .navbar-nav, .navbar-light .nav-link {
        color: #9FFFCB;
        &:hover { color: white; }
    }
    .navbar-brand {
        font-size: 1.4em;
        color: #9FFFCB;
        &:hover { color: white; }
    }
    .form-search {
        position: absolute !important;
        left: 15%;
        right: 55%;
    }
    .logoutBtn {        
        font-size: 16px;
        border: none;
        outline: none;
        text-align: center;
        text-decoration: none;
        display: inline-block;
        color: black;
        background-color: #4CAF50;
        padding: 6px 16px;
        font-family: inherit;
        margin: 0;
        border-radius: 3px;
        &:hover { color: white; }
    }
`;

const StyledSideNav = styled.div`
    position: fixed;     /* Fixed Sidebar (stay in place on scroll and position relative to viewport) */
    height: 100%;
    width: 225px;     /* Set the width of the sidebar */
    z-index: 1;      /* Stay on top of everything */
    top: 3.4em;      /* Stay at the top */
    background-color: #222; /* Black */
    overflow-x: hidden;     /* Disable horizontal scroll */
    padding-top: 10px;
`;
