import {Card, Button} from 'react-bootstrap'; 
import './Card.css'; 
import lotr from './images/lord_of_the_rings.jpg'
import hobbit from '../images/the_hobbit.jpg'
import grinch from '../images/the_grinch.jpg'
import sing from '../images/sing.jpg'
import insideOut from '../images/inside_out.jpg'


export default function ProgramCard(){
    const cardContent = [
        {image: lotr, title: "The Lord of the Rings: The Fellowship of the Ring", service: "Netflix", genre: "Fantasy/Adventure", year:"2001"}, 
        {image: hobbit, title: "The Hobbit: The Desolation of Smaug", service: "Netflix", genre: "Fantasy/Adventure", year:"2013"}, 
        {image: grinch, title: "The Grinch", service: "Netflix", genre: "Family/Comedy", year:"2000"}, 
        {image: sing, title: "Sing", service: "Netflix", genre: "Family/Musical", year:"2016"}, 
        {image: insideOut, title: "Inside Out ", service: "Netflix", genre: "Family/Comedy", year:"2015"}, 
        {image: lotr, title: "The Lord of the Rings: The Fellowship of the Ring", service: "Netflix", genre: "Fantasy/Adventure", year:"2001"}, 
        {image: hobbit, title: "The Hobbit: The Desolation of Smaug", service: "Netflix", genre: "Fantasy/Adventure", year:"2013"}, 
        {image: grinch, title: "The Grinch", service: "Netflix", genre: "Family/Comedy", year:"2000"}, 
        {image: sing, title: "Sing", service: "Netflix", genre: "Family/Musical", year:"2016"}, 
        {image: insideOut, title: "Inside Out ", service: "Netflix", genre: "Family/Comedy", year:"2015"}
    ]; 

    const renderCard = (card, index) => {
        return (
            <Card style={{ width: '11rem'}} key={index} className="card">
                <Card.Img variant="top" src={card.image} />
                <Card.Body>
                    <Card.Text style={{height: '0rem'}} className="cardTitle">{card.title}</Card.Text>
                    <Card.Text style={{height: '0rem'}} className="cardText">{card.service}</Card.Text>
                    <Card.Text style={{height: '0rem'}} className="cardText">{card.genre}</Card.Text>
                    <Card.Text style={{height: '0rem'}} className="cardText">{card.year}</Card.Text>
                    <Button className="favBtn">+</Button>
                </Card.Body>
            </Card>
        ); 
    }; 
    return <div className="grid">{cardContent.map(renderCard)}</div>; 
};
