import React, { useRef, useState, useEffect } from 'react'
import firebase from "firebase/app";
import "firebase/auth";

import { Form, Button, Card, Alert } from 'react-bootstrap'
import { Link, useHistory } from "react-router-dom"
import Dashboard from '../Dashboard'

export default function ProfileEdit() {
    var user = firebase.auth().currentUser;
    var userEmail = user.email;

    const [userDetails, setUserDetails] = useState([]);

    const emailRef = useRef()
    const [email, setEmail] = useState('')
    const passwordRef = useRef()
    const [password, setPassword] = useState('')
    const nameRef = useRef()
    const [name, setName] = useState('')
    const usernameRef = useRef()
    const [username, setUsername] = useState('')
    const [error, setError] = useState('')
    // const [loading, setLoading] = useState(false)
    const history = useHistory()

    useEffect(() => {
        fetch(`/profile?Email=${userEmail}`).then(response =>
            response.json()).then(data => {
                console.log(data)
                setUserDetails([data['userInfo']])
            });
    }, []);

    async function updateUserDatabase() {
        if (nameRef.current.value !== '') {
            setName = nameRef.current.value
        } else {
            console.log("Users Name")
            let result = userDetails.map(a => a.Name);
            console.log(result)

        }
        if (usernameRef.current.value !== '') {
            setUsername = usernameRef.current.value
        } else {

        }
        if (emailRef.current.value !== '') {
            setEmail = emailRef.current.value
        } else {
            const user = firebase.auth().currentUser;
            setEmail = user.email;
        }
        if (passwordRef.current.value !== '') {
            setPassword = passwordRef.current.value
        } else {

        }
        // const request = {
        //     method: 'POST',
        //     headers: { 'Content-Type': 'application/json' },
        //     body: JSON.stringify({
        //         Email: email,
        //         Password: passwordRef.current.value
        //     })
        // }
        // const response = await fetch('/signup', request);
        // if (response.ok) {
        //     console.log('Review added')

        // } else {
        //     console.log('Not successful')
        // }
    }

    return (
        <>
            <Dashboard />
            <Card className="d-flex align-items-center justify-content-center"
                style={{ minHeight: "100vh" }}>
                <div className="w-100" style={{ maxWidth: '400px' }}>
                    <Card.Body>
                        <h2 className="text-center mb-4">Edit Profile</h2>
                        {error && <Alert variant="danger">{error}</Alert>}
                        <Form>
                            {/* // onSubmit={updateUserDatabase}> */}
                            {/* Name */}
                            <Form.Group id="name">
                                <Form.Label>Name</Form.Label>
                                <Form.Control type="text" ref={nameRef} />
                            </Form.Group>
                            {/* Username */}
                            <Form.Group id="username">
                                <Form.Label>Username</Form.Label>
                                <Form.Control type="text" ref={usernameRef} />
                            </Form.Group>
                            {/* Email */}
                            <Form.Group id="email">
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="email" ref={emailRef} />
                            </Form.Group>
                            {/* Password */}
                            <Form.Group id="password">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" ref={passwordRef} />
                            </Form.Group>
                            <Button onClick={updateUserDatabase} className="w-100" >Sign Up</Button>
                        </Form>
                    </Card.Body>
                    <div className="w-100 text-center mt-2">
                        Don't want to edit profile? <Link to="/profile">Cancel</Link>
                    </div>
                </div>
            </Card>
        </>
    )
}
