import React from 'react'
import Dashboard from '../Dashboard'
import {Button} from 'react-bootstrap'
import styled from 'styled-components'
import CrewCard from '../../cards/CrewCard'
import ReviewCard from '../../cards/ReviewCard'

export default function IndivMovie() {
    return (
        <>
            <Dashboard/>
            <div>
                <Top>
                    <img style={{width: "250px", height: "350px"}} src="https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcT9J7XACn3tlD6v4UXRMvT2wJN8FGCCPeh8U3RkZ6__tR4wGhSo" alt=""/>
                    <div>
                        <Title><h1>Lord of the Rings</h1></Title>
                            
                    </div>
                </Top>
                <RatingText><h3>Rating: 8.8/10</h3></RatingText>
                <AddFavBtn><Button>Add To Favourites</Button></AddFavBtn>
                <Header><h2>Crew</h2></Header>
                <Scroll><CrewCard/></Scroll>
                <Header><h2>Reviews <AddRevBtn><Button>Add Review</Button></AddRevBtn></h2></Header>
                <ReviewCard/>

            </div>

        </>
    )
}

const Top = styled.div`
    display: flex; 
    justify-content: space-around; 
    margin-top: 50px;    
    margin-left: 35px; 
`
const Title = styled.div `
    margin-right: 0px; 
`

const Header = styled.div`
    margin-left: 240px; 
    margin-top: 50px;
`
const Image = styled.div`
`
const AddFavBtn = styled.div `
    margin-left: 270px; 
    margin-top: 20px;
`

const AddRevBtn = styled.div `
    margin-left: 750px; 
`

const Scroll = styled.div`
    height: 300px; 
    padding-bottom: 50px; 
    overflow: hidden; 
    overflow-y: auto; 
`

const RatingText = styled.div `
    margin-left: 270px; 
    margin-top: 20px;
`

const Text = styled.div`
    margin-top: 20px;
    margin-left: 40px;
`
