import React from 'react'
import Dashboard from '../Dashboard'
import ProgramCard from '../../cards/ProgramCard'
import CrewCard from '../../cards/CrewCard'
import styled from 'styled-components'

export default function Favourites() {
    const [programDetails, setProgramDetails] = useState([]);
    const [programCrew, setProgramCrew] = useState([]); 
    const [programReviews, setProgramReviews] = useState([]); 

    var Uid = props.location.state['Uid']
    var favBtnStatus = 'Add To Favourites'

    useEffect(()=>{
        fetch(`/programdetails?Uid=${Uid}`).then(response => 
            response.json()).then(data => { 
                setProgramDetails(data[0]); 
                setProgramCrew(data[1])
                setProgramReviews(data[2])
                //console.log(props.location.state); 
                //console.log(props.location.state['state'])
            }); 
    }, []); 
    var userEmail = 'dennis.scott@email.com'
    
    return (
        <>
            <Dashboard/>
            <Header>
                <h1>My Favourite Crew</h1>
            </Header>
            <Scroll><CrewCard /></Scroll>
            <Header2>
                <h1>My Favourite Movies</h1>
            </Header2>
            <Scroll><ProgramCard /></Scroll>
            <Header2>
                <h1>My Favourite TV Shows</h1>
            </Header2>
            <Scroll><ProgramCard /></Scroll>
        </>
    )
}

const Header = styled.div`
    margin-left: 240px; 
    margin-top: 70px;
`
const Header2 = styled.div`
    margin-left: 240px; 
    margin-top: 50px;
`

/*
 * References: 
 * Making Scrollable sections within a page: 
 *      How To Make A Div Vertically Scrollable Using CSS https://www.youtube.com/watch?v=uB1KzjV0IhM 
 */
const Scroll = styled.div`
    height: 400px; 
    padding-bottom: 50px; 
    overflow: hidden; 
    overflow-y: auto; 
`
