import React, { useState } from 'react'
import { Nav, Navbar, Form, FormControl, Button, Alert } from 'react-bootstrap'
import { useAuth } from "../../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"
import styled from 'styled-components'

export default function NavigationBar() {
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth()
    const history = useHistory()

    async function handleLogout() {
        setError('')

        try {
            await logout()
            history.pushState('/login')
        } catch {
            setError('Failed to log out')
        }
    }
    return (
        <Container>
            {error && <Alert variant="danger">{error}</Alert>}
            <Navbar expand="lg">
                <Navbar.Brand href="/">MOVIESRUS</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Form className="form-center">
                    <FormControl type="text" placeholder="Search" className="" />
                </Form>
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ml-auto">
                        <Nav.Item><Nav.Link href="/">Profile</Nav.Link></Nav.Item>
                        <Nav.Item>
                            <Button class="logoutBtn" variant="link" onClick={handleLogout}>Log Out</Button>
                        </Nav.Item>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        </Container>
    )
}



const Container = styled.div`
    .navbar { background-color: #222; }
    a, .navbar-nav, .navbar-light .nav-link {
        color: #9FFFCB;
        &:hover { color: white; }
    }
    .navbar-brand {
        font-size: 1.4em;
        color: #9FFFCB;
        &:hover { color: white; }
    }
    .form-center {
        position: absolute !important;
        left: 25%;
        right: 25%;
    }
    .logoutBtn {        background-color: #4CAF50;

  text-align: center;
  text-decoration: none;
  display: inline-block;

  font-size: 16px;
        border: none;
        outline: none;
        color: #9FFFCB;
        padding: 6px 16px;
        font-family: inherit;
        margin: 0;
        border-radius: 3px;
        &:hover { color: white; }

    }
`;
