import React, {useState, useEffect} from 'react'
import Dashboard from '../Dashboard'
import {Button} from 'react-bootstrap'
import styled from 'styled-components'
import ProgramCard from '../../cards/ProgramCard'

export default function CrewDetails() {
    const [crewDetails, setCrewDetails] = useState([])

    var Cid = '6e31c1e0-93a0-11eb-a502-00ff9885c460'

    useEffect(()=>{
        fetch(`/crewdetails?Cid=${Cid}`).then(response => 
            response.json()).then(data => { 
                console.log(data); 
                setCrewDetails(data); 
            }); 
    }, []); 

    return (
        <>
            <Dashboard/>
            {crewDetails.map}

        </>
    )
}

const Top = styled.div`
    display: flex; 
    margin-top: 50px;
    margin-left: 180px; 
`
const Title = styled.div `
    margin-right: 0px; 
    margin-bottom: 20px; 
`
const Text = styled.div `
    margin-left: 30px; 
    margin-top: 0px; 
    margin-bottom: 20px; 
`

const Header = styled.div`
    margin-left: 240px; 
    margin-top: 50px;
`
const Image = styled.div` 
    margin-left: 90px; 
    margin-right: 0px;   
`
const AddFavBtn = styled.div `
    margin-left: 270px; 
    margin-top: 20px;
`

/*
 * References: 
 * Making Scrollable sections within a page: 
 *      How To Make A Div Vertically Scrollable Using CSS https://www.youtube.com/watch?v=uB1KzjV0IhM 
 */
const Scroll = styled.div`
    height: 400px; 
    overflow: hidden; 
    overflow-y: auto; 
`
