                <Card.Body>
                    <CardTitle><Card.Text>{card.name}</Card.Text></CardTitle>
                    <Card.Text style={{height: '0rem'}} className="">{card.location}</Card.Text>
                    <Card.Text style={{height: '0rem'}} className="">${card.price}/month</Card.Text>
                    <Card.Link href="">Remove Service</Card.Link>
                    </Card.Body>
        </Card>*/
export default function SubCard(){
    const cardContent = [
        {image: "https://cdn.vox-cdn.com/thumbor/QuS2QKQys3HhosKiV-2IuKhphbo=/39x0:3111x2048/1400x1050/filters:focal(39x0:3111x2048):format(png)/cdn.vox-cdn.com/uploads/chorus_image/image/49901753/netflixlogo.0.0.png", name: "Netflix", location: "Canada", price:"9.99 CAD"}, 
        {image: "", name: "Amazon Prime Video", location: "Canada", price:"7.99 CAD"}, 
        {image: "", name: "Disney+", location: "Canada", price:"11.99"}, 
        {image: "", name: "Apple TV+", location: "Canada", price:"5.99 CAD"}
    ]; 

    const renderCard = (card, index) => {
        return (
            <div>
                <Box>
                    T
                    <Text>
                        <h6>{card.location}</h6>
                        <h6>{card.price} /month</h6>
                    </Text>
                    <RemoveLink><Link>Remove Subscription</Link></RemoveLink>
                </Box>
            </div>
        ); 
    }; 
    return <div>{cardContent.map(renderCard)}</div>; 
}; 

const CardTitle = styled.div`
    font-size: medium;
    font-weight: bold;
`

const Box = styled.div`
    border-radius: 10px;
    box-shadow: 0px 12px 18px -6px rgba(0, 0, 0, 0.3);  
    margin-left: 400px; 
    margin-top: 30px; 
    margin-bottom: 20px; 
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-item: center; 
    background: white;
    height: 150px; 
    width: 800px; 
`
const Title = styled.div`

`
const Text = styled.div`
    margin-left: 50px; 
    margin-top: 0px; 
`
const RemoveLink = styled(Link) `
    margin-left: 650px; 
`
