import React from 'react'
import {Button} from 'react-bootstrap'
import styled from 'styled-components'
import {Form, Input} from 'semantic-ui-react'

export default function AddReviewForm(props){

    const [review, setReview] = useState()
    /*References: 
        * Creating a add review popup form where users can add a review: 
            * Build a POPUP component in React JS ~ A Beginner Tutorial with React https://www.youtube.com/watch?v=i8fAO_zyFAM */
    return (props.trigger)?(
        <Popup>
            <div>
                <ReviewForm>
                    <Form>

                    </Form>
                    <div>
                        <Button onClick={() => props.setTrigger(false)}>X</Button>
                        <SubmitBtn><Button>Submit</Button></SubmitBtn>
                        {props.children}   
                    </div>  
                </ReviewForm>        
            </div>
        </Popup>
    ) : ""; 
}; 

const Popup = styled.div `
    position: fixed; 
    top: 0; 
    left: 0; 
    width: 100%; 
    height: 100vh;  
    background-color: rgba(0, 0, 0, 0.2); 
    display: flex; 
    justify-content: center; 
    align-items: center; 
`

const ReviewForm = styled.div `
    position: relative; 
    padding: 32px; 
    width: 700px;  
    background-color: white; 
`
const SubmitBtn = styled.div`
    position: absolute; 
    top: 16px;
    right 16px 
`
