import React, { useState } from 'react'
import { Nav, Navbar, Form, Alert } from 'react-bootstrap'
import { useAuth } from "../../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"
import { Input, Label, Menu, Button } from 'semantic-ui-react'
import styled from 'styled-components'

import SideBarItem from "./SideBarItem"

export default function Dashboard() {
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth()
    const history = useHistory()

    async function handleLogout() {
        setError('')

        try {
            await logout()
            history.pushState('/login')
        } catch {
            setError('Failed to log out')
        }
    }
    return (
        <Container>
            <Header>
                {error && <Alert variant="danger">{error}</Alert>}
                <Navbar expand="lg">
                    <Navbar.Brand href="/">MOVIESRUS</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Form className="form-search">
                        <Input icon="search" placeholder="Search..." />
                    </Form>
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="ml-auto">
                            <Nav.Item><Nav.Link href="/">Profile</Nav.Link></Nav.Item>
                            <Nav.Item>
                                <Button primary class="logoutBtn" variant="link" onClick={handleLogout}>Logout</Button>
                            </Nav.Item>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
            </Header>
            <StyledSideNav>
                <Menu borderless vertical stackable fixed="left" className="side-nav">
                    <SideBarItem highlight={true} label='Home' icon='home' />
                    <SideBarItem label='Trending' icon='fire' />
                    <SideBarItem label='Followers' icon='spy' />
                    <SideBarItem label='History' icon='history' />
                    <SideBarItem label='Watch later' icon='clock' />
                    <SideBarItem label='Liked videos' icon='thumbs up' />
                    <SideBarItem label='Movies and Shows' icon='film' />
                    <SideBarItem label='Report history' icon='flag' />
                    <SideBarItem label='Help' icon='help circle' />
                    <SideBarItem label='Send feedback' icon='comment' />
                </Menu>
            </StyledSideNav>
        </Container>
    )
}


const Container = styled.div``;

const Header = styled.div`
    .navbar { background-color: #222; }
    a, .navbar-nav, .navbar-light .nav-link {
        color: #9FFFCB;
        &:hover { color: white; }
    }
    .navbar-brand {
        font-size: 1.4em;
        color: #9FFFCB;
        margin-left: 25px;
        &:hover { color: white; }
    }
    .form-search {
        position: absolute !important;
        left: 15%;
        right: 55%;
    }
`;

const StyledSideNav = styled.div`
    position: fixed;     
    height: 100%;
    width: 215px;     
    z-index: 1;      
    top: 3.4em;      
    background-color: #222; 
    overflow-x: hidden;     
    padding-top: 10px;

    .ui.menu.fixed.side-nav {
        background-color: #f5f5f5;
        margin-left: 5px;
        margin-top: 50px;
        overflow-y: auto;
        padding-bottom: 64px;

        .sidebar-item:hover {
            background: #ebebeb;
            }
        }

        .side-nav.ui.vertical.menu {
            width: 200px;
        }
`;
