import React, {useState, useEffect} from 'react'
import Dashboard from '../Dashboard'
import {Button} from 'react-bootstrap'
import styled from 'styled-components'
import CrewCard from '../../cards/CrewCard'
import ReviewCard from '../../cards/ReviewCard'
import AddReviewForm from '../../forms/AddReviewForm'

export default function IndivMovie() {
    const [addReviewBtn, setAddReviewBtn] = useState(false); 

    const [programDetails, setProgramDetails] = useState([]); 
    var Uid = '1f3cd355-93ca-11eb-a502-00ff9885c460'

    useEffect(()=>{
        fetch(`/programdetails?Uid=${Uid}`).then(response => 
            response.json()).then(data => { 
                console.log(data); 
                setProgramDetails(data); 
            }); 
    }, []); 

    return (
        <>
            <Dashboard/>
            {programDetails}
        </>
    )
}

const Top = styled.div`
    display: flex; 
    margin-top: 50px;
    margin-left: 180px; 
`
const Title = styled.div `
    margin-right: 0px; 
    margin-bottom: 20px; 
`

const Header = styled.div`
    margin-left: 240px; 
    margin-top: 30px;
`
const Image = styled.div`
    margin-left: 90px; 
    margin-right: 0px; 
`
const AddFavBtn = styled.div `
    margin-left: 270px; 
    margin-top: 20px;
`

const AddRevBtn = styled.div `
    margin-left: 750px; 
`

/*
 * References: 
 * Making Scrollable sections within a page: 
 *      How To Make A Div Vertically Scrollable Using CSS https://www.youtube.com/watch?v=uB1KzjV0IhM 
 */
const Scroll = styled.div`
    height: 300px; 
    padding-bottom: 50px; 
    overflow: hidden; 
    overflow-y: auto; 
`

const RatingText = styled.div `
    margin-left: 270px; 
    margin-top: 20px;
`

const Text = styled.div `
    margin-left: 30px; 
    margin-top: 0px; 
    margin-bottom: 10px; 
`

const SynopText = styled.div `
    margin-left: 270px; 
    margin-top: 10px; 
    width: 1010px; 
    display: flex; 
    justify-content: space-around; 
`
