import React from 'react'
import styled from 'styled-components'
import {Link} from 'react-router-dom'

export default function SubCard({services}){
    const cardContent = [
        {image: "https://images.justwatch.com/icon/207360008/s100", name: "Netflix", location: "Canada", price:"9.99 CAD"}, 
        {image: "https://images.justwatch.com/icon/5278783/s100", name: "Amazon Prime Video", location: "Canada", price:"7.99 CAD"}, 
        {image: "https://images.justwatch.com/icon/152862153/s100", name: "Disney+", location: "Canada", price:"11.99"}, 
        {image: "https://images.justwatch.com/icon/59562423/s100", name: "Apple TV+", location: "Canada", price:"5.99 CAD"}
    ]; 

    /**
     * References: 
     * To populate each card with information from card content object: 
     *      Dynamically Create Cards In ReactJS Using React-Bootstrap https://www.youtube.com/watch?v=IhWFs0diAPE
     */
    const renderCard = (card, index) => {
        return (
            <div>
                <Box>
                    <Top>
                        <img style={{width: "60px", height: "60px", borderRadius: "10px", marginLeft: "15px", marginTop}} src={card.Logo} alt=""/>   
                        <div>
                            <h3 style={{marginTop: "0px", marginLeft: "10px", marginBottom: "0px"}}>{card.Service_name}</h3>
                        </div>
                        {/*<h3> <img style={{width: "60px", height: "60px"}} src={card.Logo} alt=""/>   {card.Service_name}</h3>*/}
                    </Top>
                    <Text>
                        <h6>{card.Location}</h6>
                        <h6>{card.Price} /month</h6>
                    </Text>
                    <RemoveLink><Link>Remove Subscription</Link></RemoveLink>
                </Box>
            </div>
        ); 
    }; 
    return <div>{services.map(renderCard)}</div>; 
}; 

/*
 * References: 
 *  To create box for every subscription service: 
 *      Build a Recipe App With React | React Tutorial For Beginners https://www.youtube.com/watch?v=U9T6YkEDkMo&list=PLDyQo7g0_nsVHmyZZpVJyFn5ojlboVEhE&index=2 */
const Box = styled.div`
    margin-top: 30px; 
    margin-bottom: 20px; 
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-item: center; 
    border-radius: 10px;
    box-shadow: 0px 12px 18px -6px rgba(0, 0, 0, 0.3);  
    margin-left: 400px; 
    background: white;
    height: 150px; 
    width: 800px; 
`
const Top = styled.div`
    display: flex;  
    margin-bottom: 0px; 
`
const Text = styled.div`
    margin-left: 70px; 
    margin-top: 0px; 
    margin-bottom: 0px; 
`
const RemoveLink = styled(Link) `
    margin-left: 650px; 
`
